mapper.update:
UPDATE product_t
SET sell_count=sell_count + #{count}
WHERE prt_id =#{prtId}

UPDATE product_t
SET num=num + #{count}
WHERE prt_id
=#{prtId}

UPDATE product_t
SET num = num - #{count}
WHERE prt_id
= #{prtId} and num > #{count}
